        <!-- Text Content -->
        <div class="text-center space-y-2">
            <h1 class="text-lg font-bold">Cookie toestemming</h1>
            <p class="text-sm">
                Wij slaan cookies op om het gebruik van ons platform te faciliteren, te verbeteren en te promoten. Ga naar ons
                <a href="/privacy-policy" class="underline text-blue-400 hover:text-blue-300">cookiebeleid</a> voor meer informatie.
            </p>
        </div>

        <!-- Buttons -->
        <div id="cookie-button-group" class="flex gap-4">
            <button id="cookie-reject-all" class="bg-red-600 hover:bg-red-500 text-white font-medium py-1 px-3 rounded-md">
                Alles afwijzen
            </button>
            <button id="cookie-accept-all" class="bg-green-600 hover:bg-green-500 text-white font-medium py-1 px-3 rounded-md">
                Alles accepteren
            </button>
            <button id="cookie-settings" class="bg-blue-600 hover:bg-blue-500 text-white font-medium py-1 px-3 rounded-md">
                Instellingen
            </button>
        </div>
