<!-- Settings Modal -->
<div id="cookie-settings-modal" class="hidden fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center">
    <div class=" bg-gradient-to-r from-orange-500 to-orange-300 text-black rounded-lg p-6 w-96">
        <h2 class="text-xl font-bold mb-4">Cookievoorkeuren</h2>
        <p class="mb-4">Beheer uw voorkeuren:</p>
        <div class="flex flex-col space-y-4">
            <label class="flex items-center">
                <input type="checkbox" disabled checked class="mr-2" />
                Noodzakelijke cookies (Altijd ingeschakeld)
            </label>
            <label class="flex items-center">
                <input type="checkbox" id="analytics-cookies" class="mr-2" />
                Analytische cookies inschakelen
            </label>
            <label class="flex items-center">
                <input type="checkbox" id="marketing-cookies" class="mr-2" />
                Marketingcookies inschakelen
            </label>
        </div>
        <div class="mt-6 flex justify-end gap-4">
            <button id="close-settings" class="bg-gray-600 hover:bg-gray-500 text-white font-medium py-2 px-4 rounded-md">
                Annuleren
            </button>
            <button id="save-settings" class="bg-yellow-500 hover:bg-yellow-400 text-black font-medium py-2 px-4 rounded-md">
                Voorkeuren opslaan
            </button>
        </div>
    </div>
</div>